frappe.ui.form.on('Project', {
  refresh(frm) {
    frm.set_query("booklet_item", function() {
      return {
        "filters": [
          ["reference_docname", "in", [frm.doc.name,'']],
          ["reference_doctype", "=", frm.doc.doctype]
        ]
      };
    });  
  }
});
